widget.menu_js:
function CheckBoxWidget(ui_creator, menu_widget) {
	var me = this;

	me.ignore_attributes = ["type", "checked"];

	//TODO: find a away to remove the value attribute

	me.init = function() {
		menu_widget.attr({
			"data-on-parse-template-widget-html-func": ui_creator.obj_var_name + ".menu_widgets_objs.checkbox.parseHtml",
			"data-on-create-template-widget-func": ui_creator.obj_var_name + ".menu_widgets_objs.checkbox.onCreateTemplateWidget",
		});

		menu_widget.children(".properties").attr({
			"data-on-open-settings-func": ui_creator.obj_var_name + ".menu_widgets_objs.checkbox.onOpenWidgetSettings",
			"data-on-after-save-settings-field-func": ui_creator.obj_var_name + ".menu_widgets_objs.checkbox.saveSettingsField",
			"data-on-before-parse-widget-settings-func": ui_creator.obj_var_name + ".menu_widgets_objs.checkbox.parseSettings",
		});
	};

	me.parseHtml = function(html_element) {
		return html_element && html_element.nodeName.toLowerCase() == "input" && ("" + html_element.getAttribute("type")).toLowerCase() == "checkbox";
	};

	me.parseSettings = function(widget, widget_settings) {
		widget_settings["is-checked"] = widget[0].hasAttribute("checked") ? 1 : 0;
		widget_settings["name"] = widget.attr("name");
		widget_settings["value"] = widget.attr("value");
	};

	me.onCreateTemplateWidget = function(widget, html_element) {
		if (!html_element)
			widget.attr("data-allow-null", "1");

		widget[0].type = "checkbox";
	};

	me.onOpenWidgetSettings = function(widget, menu_settings) {
		ui_creator.addMenuSettingsContextMenu( menu_settings.find(" > .settings-properties input") );
	};

	me.saveSettingsField = function(field, widget, status) {
		if (status) {
			field = $(field);
			var field_value = field.val();
			var sprop = field.parent();

			if (sprop.hasClass("is-checked")) {
				if (field.is(":checked")) {
					widget.attr("checked", "checked").prop("checked", true);
					//widget.addClass("checked");
				}
				else {
					widget.removeAttr("checked").prop("checked", false);
					//widget.removeClass("checked");
				}
			}
			else if (sprop.hasClass("name")) {
				if (field_value != "")
					widget.attr("name", field_value);
				else
					widget.removeAttr("name");
			}
			else if (sprop.hasClass("value")) {
				if (field_value != "")
					widget.attr("value", field_value);
				else
					widget.attr("value", "");
			}
		}

		return status;
	};
}
