include_once $EVC->getUtilPath("CMSPresentationLayerHandler"); $UserAuthenticationHandler->checkPresentationFileAuthentication($entity_path, "access"); $bean_name = $_GET["bean_name"]; $bean_file_name = $_GET["bean_file_name"]; $path = $_GET["path"]; $item_type = $_GET["item_type"]; $element_type = $_GET["element_type"]; $exists_db_drivers = false; if ($item_type == "dao") { $UserAuthenticationHandler->checkInnerFilePermissionAuthentication("vendor/dao/$path", "layer", "access"); $layers = array( $item_type => AdminMenuHandler::getDaoObjs(false, 1) ); } else if ($item_type == "lib") { $layers = array( $item_type => AdminMenuHandler::getLibObjs(false, 1) ); } else if ($item_type == "vendor") { $UserAuthenticationHandler->checkInnerFilePermissionAuthentication("vendor/$path", "layer", "access"); $layers = array( $item_type => AdminMenuHandler::getVendorObjs(false, 1) ); } else if ($item_type == "other") { $UserAuthenticationHandler->checkInnerFilePermissionAuthentication("other/$path", "layer", "access"); $layers = array( $item_type => AdminMenuHandler::getOtherObjs(false, 1) ); } else if ($item_type == "test_unit") { $UserAuthenticationHandler->checkInnerFilePermissionAuthentication("vendor/testunit/$path", "layer", "access"); $layers = array( $item_type => AdminMenuHandler::getTestUnitObjs(false, 1) ); } else { $layers = AdminMenuHandler::getLayersFiles($user_global_variables_file_path); if ($layers) { if (!empty($layers["db_layers"])) foreach ($layers["db_layers"] as $layer_name => $layer) if ($layer_name != "properties") foreach ($layer as $driver_name => $driver) if ($driver_name != "properties") { $exists_db_drivers = true; break; } if ($item_type) $layers = $layers[$item_type . "_layers"]; else if ($bean_name && $bean_file_name) { $new_layers = array(); foreach ($layers as $layer_type_name => $layer_type) foreach ($layer_type as $layer_name => $layer) { $properties = $layer["properties"]; if ($properties["bean_name"] == $bean_name && $properties["bean_file_name