widget.menu_js:
function TemplateRegionBlockWidget(ui_creator, menu_widget) {
	var me = this;
	var obj_php_widget = new PHPWidget(ui_creator, menu_widget);

	me.init = function() {
		obj_php_widget.init();

		menu_widget.removeAttr("data-on-parse-template-widget-html-func");

		menu_widget.attr({
			"data-on-create-template-widget-func": ui_creator.obj_var_name + ".menu_widgets_objs['region-block'].onCreateTemplateWidget",
		});
	};

	me.onCreateTemplateWidget = function(widget, html_element) {
		var value = prompt("What is the new region name?");
		value = value ? value : "...";

		if (!html_element)
			html_element = document.createComment('? echo $EVC->getCMSLayer()->getCMSTemplateLayer()->renderRegion("' + value + '"); ?');

		obj_php_widget.onCreateTemplateWidget(widget, html_element);
	};
}
