widget.menu_js:
function UserWidget2Widget(ui_creator, menu_widget) {
	var me = this;

	me.init = function() {
		menu_widget.attr({
			"data-on-create-template-widget-func": ui_creator.obj_var_name + ".menu_widgets_objs['user-widget-2'].onCreateTemplateWidget",
		});
	};

	me.onCreateTemplateWidget = function(widget, html_element) {
		if (!html_element)
			widget.html('User Widget 2 Example');
	};
}
