widget.menu_js:
function UserWidget1Widget(ui_creator, menu_widget) {
	var me = this;
	var default_src = "#widget_webroot_url#img/bg.gif";

	me.init = function() {
		menu_widget.attr({
			"data-on-create-template-widget-func": ui_creator.obj_var_name + ".menu_widgets_objs['user-widget-1'].onCreateTemplateWidget",
		});
	};

	me.onCreateTemplateWidget = function(widget, html_element) {
		if (!html_element) 
			widget.attr("src", default_src).css({width: "100px", height: "100px"});
	};
}
