widget.menu_js:
function BoostrapModalWidget(ui_creator, menu_widget) {
	var me = this;

	me.init = function() {
		menu_widget.attr({
			"data-on-drag-stop-func": ui_creator.obj_var_name + ".menu_widgets_objs['bs-modal'].onDropMenuWidget",
		});
	};

	me.onDropMenuWidget = function(menu_widget, widget, event, ui_obj) {
	    ui_creator.convertHtmlElementToWidget( widget.children() );
	};
}
